mapper.select:
select * from module where app_id = #{map.app_id}
and module_name like concat('%',#{map.search},'%')
order by last_updated_time desc

select * from module
where app_id = #{map.app_id}
order by module_name desc

insert.if:
update module
set app_id = #{module.appId},
module_name = #{module.moduleName},
module_desc = #{module.moduleDesc},
last_updated_time = #{module.lastUpdatedTime}
where id = #{module.id}
