mapper.select:
select * from app_id


        id = #{appId.id}


        app_name = #{appId.appName}


limit 1

insert.if:
update app_id
set app_name = #{appId.appName}, app_desc = #{appId.appDesc},
created_by = #{appId.createdBy}, created_time = #{appId.createdTime},
last_updated_by = #{appId.lastUpdatedBy}, last_updated_time = #{appId.lastUpdatedTime}
where id = #{appId.id}
